import React from 'react';
import { useGetWeightedProgressQuery } from '../slices/progresoApiSlice.js';

const ProgresoScreen = () => {
  const {
    data: userProgress,
    isLoading,
    isError,
    error
  } = useGetWeightedProgressQuery();

  // Consolidar los datos de progreso por usuario
  let consolidatedUserProgress = {};
  if (userProgress) {
    userProgress.forEach(progress => {
      const { name } = progress;
      if (!consolidatedUserProgress[name]) {
        consolidatedUserProgress[name] = { name };
      }
      // Aquí asumimos que 'progress' tiene las propiedades 'Progreso 1', 'Progreso 2', 'Progreso 3', etc.
      Object.keys(progress).forEach(key => {
        if (key.includes('Progreso')) { // Asegúrate de que esta condición coincida con las propiedades de tus objetos de progreso.
          consolidatedUserProgress[name][key] = progress[key] ? progress[key].toFixed(2) : 'N/A';
        }
      });
    });
  }

  // Convertir el objeto consolidado nuevamente en un array para el renderizado
  const progressArray = Object.values(consolidatedUserProgress);

  if (isLoading) return <div>Loading...</div>;
  if (isError) return <div>Error: {error.toString()}</div>;

  return (
    <div>
      <h1>Weighted Progress</h1>
      <table>
        <thead>
          <tr>
            <th>User Name</th>
            <th>Progreso 1</th>
            <th>Progreso 2</th>
            <th>Progreso 3</th>
          </tr>
        </thead>
        <tbody>
          {progressArray.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user['Progreso 1'] || 'N/A'}</td>
              <td>{user['Progreso 2'] || 'N/A'}</td>
              <td>{user['Progreso 3'] || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProgresoScreen;




