import React from 'react';
import { Row, Col } from 'react-bootstrap';
import Grade from "../components/Grade.jsx";
import grades from "../grade.js"; 
import { useGetGradesQuery } from '../slices/gradesApiSlice.js';

const HomeScreen = () => {
  const{data:grades,isLoading,error}=useGetGradesQuery();
  return (
    <>
      <h1>Grades</h1>
      <Row>
        <Col xs={12}>
          <table>
            <thead>
              <tr>
                <th>User</th>
                <th>Grade</th>
                <th>Start Date</th>
                <th>End Date</th>
              </tr>
            </thead>
            <tbody>
              {grades.map((grade, index) => (
                <tr key={index}>
                  <td>Thyara Vintimilla</td> {/* Usar el nombre de usuario correspondiente */}
                  <td>{grade.grade}</td>
                  <td>{grade.startDate.toLocaleDateString()}</td>
                  <td>{grade.endDate.toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </Col>
      </Row>
    </>
  );
};

export default HomeScreen;
