import React, { useState } from 'react';
import { useGetGradesQuery } from '../slices/gradesApiSlice.js'; // Asegúrate de que la ruta sea correcta
import { Button, Form, FormGroup, Label, Input } from 'reactstrap'; // Asumiendo que usas reactstrap

const FilterScreen = () => {
  const { data: grades } = useGetGradesQuery();
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredCount, setFilteredCount] = useState(0);

  const handleFilter = () => {
    if (grades && startDate && endDate) {
      const start = new Date(startDate);
      const end = new Date(endDate);
      const count = grades.filter(grade => {
        const gradeDate = new Date(grade.startDate);
        return gradeDate >= start && gradeDate <= end;
      }).length;
      setFilteredCount(count);
    }
  };

  return (
    <div>
      <h2>Filter Grades</h2>
      <Form>
        <FormGroup>
          <Label for="startDate">Start Date</Label>
          <Input
            type="date"
            name="startDate"
            id="startDate"
            value={startDate}
            onChange={e => setStartDate(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="endDate">End Date</Label>
          <Input
            type="date"
            name="endDate"
            id="endDate"
            value={endDate}
            onChange={e => setEndDate(e.target.value)}
          />
        </FormGroup>
        <Button onClick={handleFilter}>Filter</Button>
      </Form>
      <p>Number of grades in selected range: {filteredCount}</p>
    </div>
  );
};

export default FilterScreen;
