import React from 'react';
import { Row, Col, Table } from 'react-bootstrap'; // Importamos Table de react-bootstrap
import { useGetGradesQuery } from '../slices/gradesApiSlice.js'; // Asegúrate de que esta es la ruta correcta

const HomeScreen = () => {
  const { data: grades, isLoading, error } = useGetGradesQuery();

  if (isLoading) return <h2>Loading...</h2>;
  if (error) return <div>{error?.data?.message || error.error}</div>;

  return (
    <>
      <h1>Grades</h1>
      <Row>
        <Col xs={12}>
          <Table striped bordered hover> {/* Usamos Table para una tabla más estilizada */}
            <thead>
              <tr>
                <th>User ID</th> {/* Cambia a User Name si tienes los nombres disponibles */}
                <th>Grade</th>
                <th>Start Date</th>
                <th>End Date</th>
              </tr>
            </thead>
            <tbody>
              {grades.map(grade => (
                <tr key={grade._id}>
                  <td>{userMap[grade.user]}</td> {/* Asumiendo que 'user' es un ID de usuario. Deberías buscar el nombre del usuario con este ID. */}
                  <td>{grade.grade}</td>
                  <td>{new Date(grade.startDate).toLocaleDateString()}</td>
                  <td>{new Date(grade.endDate).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Col>
      </Row>
    </>
  );
};

export default HomeScreen;

