import React, { useState } from 'react';
import { useGetGradesQuery } from '../slices/gradesApiSlice.js';

const FilterScreen = () => {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const { data: grades } = useGetGradesQuery();

  const handleStartDateChange = (e) => {
    setStartDate(e.target.value);
  };

  const handleEndDateChange = (e) => {
    setEndDate(e.target.value);
  };

  const getFilteredGradesCount = () => {
    const start = new Date(startDate);
    const end = new Date(endDate);

    return grades.filter(grade => {
      const gradeDate = new Date(grade.startDate);
      return gradeDate >= start && gradeDate <= end;
    }).length;
  };

  // Estilos en línea
  const filterScreenStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '50vh',
  };

  const filterFormStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '20px',
    marginBottom: '20px',
  };

  const buttonStyle = {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  };

  const inputStyle = {
    padding: '10px',
    fontSize: '16px',
  };

  return (
    <div style={filterScreenStyle}>
      <h1>Filter Grades by Date</h1>
      <div style={filterFormStyle}>
        <label htmlFor="start-date">Start Date:</label>
        <input 
          style={inputStyle}
          type="date" 
          id="start-date" 
          value={startDate} 
          onChange={handleStartDateChange} 
        />
        <label htmlFor="end-date">End Date:</label>
        <input 
          style={inputStyle}
          type="date" 
          id="end-date" 
          value={endDate} 
          onChange={handleEndDateChange} 
        />
        <button style={buttonStyle} onClick={getFilteredGradesCount}>
          Show Grades Count
        </button>
      </div>
      {startDate && endDate && (
        <div>
          <p>Number of grades between {startDate} and {endDate}: {getFilteredGradesCount()}</p>
        </div>
      )}
    </div>
  );
};

export default FilterScreen;
