import React, { useEffect, useState } from 'react';
import { Row, Col, Table } from 'react-bootstrap';
import { useGetGradesQuery } from '../slices/gradesApiSlice.js';
import { useGetUserDetailsQuery } from '../slices/userApiSlice.js'; // Asegúrate de que la ruta sea correcta

const HomeScreen = () => {
  const { data: grades, isLoading, error } = useGetGradesQuery();
  const [userDetails, setUserDetails] = useState({});

  // Este hook es para obtener los detalles de un solo usuario
  const { data: userData, isFetching: isUserFetching } = useGetUserDetailsQuery();

  // Aquí asumimos que tenemos el userId disponible para cada grade
  // y llamamos al hook useGetUserDetailsQuery para cada uno de ellos
  useEffect(() => {
    if (grades) {
      grades.forEach(grade => {
        const { data: user } = useGetUserDetailsQuery(grade.user);
        if (user) {
          setUserDetails(prevDetails => ({
            ...prevDetails,
            [grade.user]: user.name
          }));
        }
      });
    }
  }, [grades, useGetUserDetailsQuery]);

  if (isLoading || isUserFetching) return <h2>Loading...</h2>;
  if (error) return <div>{error?.data?.message || error.error}</div>;

  return (
    <>
      <h1>Grades</h1>
      <Row>
        <Col xs={12}>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>User Name</th>
                <th>Grade</th>
                <th>Start Date</th>
                <th>End Date</th>
              </tr>
            </thead>
            <tbody>
              {grades && grades.map(grade => (
                <tr key={grade._id}>
                  <td>{userDetails[grade.user] || 'Unknown User'}</td>
                  <td>{grade.grade}</td>
                  <td>{new Date(grade.startDate).toLocaleDateString()}</td>
                  <td>{new Date(grade.endDate).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Col>
      </Row>
    </>
  );
};

export default HomeScreen;


