import React from 'react';
import { useGetWeightedProgressQuery } from '../slices/progresoApiSlice.js';

const ProgresoScreen = () => {
  const { data: userProgress, isLoading, error } = useGetWeightedProgressQuery();
  
  const consolidatedUserProgress = userProgress ? userProgress.reduce((acc, curr) => {
    if (!acc[curr.name]) {
      acc[curr.name] = { name: curr.name };
    }
    acc[curr.name][curr.progresoName] = curr.weightedAverage;
    return acc;
  }, {}) : {};

  // Convertir el objeto consolidado nuevamente en un array para el renderizado
  const progressArray = Object.values(consolidatedUserProgress);

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div>
      <h1>Weighted Progress</h1>
      <table>
        <thead>
          <tr>
            <th>User Name</th>
            <th>Progreso 1</th>
            <th>Progreso 2</th>
            <th>Progreso 3</th>
          </tr>
        </thead>
        <tbody>
          {progressArray.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user['Progreso 1'] || 'N/A'}</td>
              <td>{user['Progreso 2'] || 'N/A'}</td>
              <td>{user['Progreso 3'] || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProgresoScreen;



