import { GRADES_URL,PROGRESO_URL } from "../constants.js";
import { apiSlice } from "./apiSlice.js";
//l贸gica de interacci贸n con APIs
export const gradesApiSlice=apiSlice.injectEndpoints({
    endpoints:(builder)=>({
        getGrades: builder.query({
            query: () =>({
                url: GRADES_URL,
            }),
            providesTags: ['Grade'],
            keepUnusedDataFor: 5
        }),
        getWeightedProgress: builder.query({
            query: () => PROGRESO_URL,
            providesTags: ['Grade'],
          }),
    }),
});

export const {useGetGradesQuery,useGetWeightedProgressQuery}=gradesApiSlice;
