import React from 'react';
import { Row, Col } from 'react-bootstrap';
import Grade from "../components/Grade.jsx";
import grades from "../grade.js"; 
import { useGetGradesQuery } from '../slices/gradesApiSlice.js';

const HomeScreen = () => {
  
  const{data:grades,isLoading,error}=useGetGradesQuery();
  return (
    <>
    {isLoading ?(
      <h2>Loading...</h2>
    ): error ?(<div>{error?.data?.message || error.error}</div>):
    (<>
      <h1>Grades</h1>
      <Row>
        {grades.map((grade) => (
          <Col key={grade._id} sm={12} md={6} lg={4} xl={3}>
            <Grade grade={grade} />
          </Col>
        ))}
      </Row>
    </>)}
    </>
  );
};

export default HomeScreen;

