import React from 'react';
import { Row, Col } from 'react-bootstrap';
import { useGetGradesQuery } from '../slices/gradesApiSlice.js';
import { useGetProgresosQuery } from '../slices/progresoApiSlice.js';
import '../assets/styles/index.css'; // Asegúrate de que la ruta sea correcta

const consolidateProgressData = (progressData) => {
  const usersProgress = {};

  progressData.forEach(item => {
    const { name } = item;
    if (!usersProgress[name]) {
      usersProgress[name] = { name, 'Progreso 1': 'N/A', 'Progreso 2': 'N/A', 'Progreso 3': 'N/A' };
    }

    // Este paso asume que el nombre del progreso viene en el formato "Progreso Progreso X"
    const progressName = Object.keys(item).find(key => key.startsWith('Progreso Progreso'));
    if (progressName) {
      const progressNumber = progressName.split(' ')[2]; // Esto toma el número X de "Progreso Progreso X"
      usersProgress[name][`Progreso ${progressNumber}`] = item[progressName].toFixed(2);
    }
  });

  return Object.values(usersProgress); // Convertimos el objeto en un array para usarlo en el renderizado
};
const ProgresoScreen = () => {
  const {
    data: userProgress,
    isLoading,
    isError,
    error
  } = useGetWeightedProgressQuery();

  // Aquí transformas los datos recibidos para que tengan la estructura deseada
  const consolidatedData = !isLoading && !isError ? consolidateProgressData(userProgress) : [];

  if (isLoading) return <div>Loading...</div>;
  if (isError) return <div>Error: {error.toString()}</div>;

  // Renderizado de la tabla con datos de progreso
  return (
    <div>
      <h1>Weighted Progress</h1>
      <table>
        <thead>
          <tr>
            <th>User Name</th>
            <th>Progreso 1</th>
            <th>Progreso 2</th>
            <th>Progreso 3</th>
          </tr>
        </thead>
        <tbody>
          {consolidatedData.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user['Progreso 1']}</td>
              <td>{user['Progreso 2']}</td>
              <td>{user['Progreso 3']}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};


export default ProgresoScreen;
