import React from 'react';
import { useGetWeightedProgressQuery } from '../slices/progresoApiSlice.js';

const ProgresoScreen = () => {
  const {
    data: userProgress,
    isLoading,
    isError,
    error
  } = useGetWeightedProgressQuery();

  // Consolidar los datos de progreso por usuario
  const consolidatedUserProgress = userProgress ? userProgress.reduce((acc, curr) => {
    // Extrae el nombre del usuario y el progreso de los datos actuales
    const { name } = curr;
    const progressKeys = Object.keys(curr).filter(key => key.startsWith('Progreso'));

    // Si el usuario no está en el acumulador, inicialízalo
    if (!acc[name]) {
      acc[name] = { name, 'Progreso 1': 'N/A', 'Progreso 2': 'N/A', 'Progreso 3': 'N/A' };
    }

    // Asigna el valor de progreso al usuario en el acumulador
    progressKeys.forEach(progressKey => {
      acc[name][progressKey] = curr[progressKey].toFixed(2);
    });

    return acc;
  }, {}) : {};

  // Convertir el objeto consolidado nuevamente en un array para el renderizado
  const progressArray = Object.values(consolidatedUserProgress);

  // Manejo del estado de carga y error
  if (isLoading) return <div>Loading...</div>;
  if (isError) return <div>Error: {error.toString()}</div>;

  // Renderizado de la tabla con datos de progreso
  return (
    <div>
      <h1>Weighted Progress</h1>
      <table>
        <thead>
          <tr>
            <th>User Name</th>
            <th>Progreso 1</th>
            <th>Progreso 2</th>
            <th>Progreso 3</th>
          </tr>
        </thead>
        <tbody>
          {progressArray.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user['Progreso 1']}</td>
              <td>{user['Progreso 2']}</td>
              <td>{user['Progreso 3']}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProgresoScreen;





